import React from "react"
import Flexbox from "flexbox-react"

import "./Chatbot.css"

function Chatbot (props) {
  return (
    <div>
      <Flexbox className="chatbot" flexDirection="row">
        <Flexbox className="channels" flexGrow={1}>
          <Channels channels={props.data}/>
        </Flexbox>
        <Flexbox className="chats" flexGrow={5}>
          <Chats />
        </Flexbox>
      </Flexbox>
    </div>
  )
}

function Channels (props) {
  const channels = props.channels
  const listChannels = channels.map((channel, index) =>
    <li key={channel.id}>
      <Channel channel={channel} />
    </li>
  )
  return (
    <nav className="channel-navigation">
      <div className="channel-header">
        Channellist
      </div>
      {/* <ul className="channel-list">
        {listChannels}
      </ul> */}
    </nav>
  )
}

const Channel = props => (
  <button className="channel-button">
    <div>
      {props.channel.title}
    </div>
    <div>
      status: {props.channel.status}
    </div>
  </button>
)

function Chats () {
  return (
    <div>
      <header>
        Chats
      </header>
    </div>
  )
}

export default Chatbot
