import React from "react"
import Flexbox from "flexbox-react"

import "./Chatbot.css"
import Chats from "./Chats"

class Chatbot extends React.Component {
  state = {
      activeChannel
  }

  // use arrow function to get parameter from onCLick function
  toggleChannel = clickedChannel => e => {
    this.setState({activeChannel: clickedChannel})
  }

  render() {
    return (
      <div>
        {this.state.activeChannel.title}
        <Flexbox className="chatbot-flexbox" flexDirection="row">
          <Flexbox className="channels-flexbox" flexGrow={1}>
            <Channels channels={this.props.data} toggleChannel={this.toggleChannel}/>
          </Flexbox>
          <Flexbox className="chats-flexbox" flexGrow={5}>
            <Chats toggleChatbot={this.props.toggleChatbot}/>
          </Flexbox>
        </Flexbox>
      </div>
    )
  }
}

function Channels (props) {
  const channels = props.channels
  const listChannels = channels.map((channel, index) =>
    <li key={channel.id}>
      <Channel channel={channel} toggleChannel={props.toggleChannel} />
    </li>
  )
  return (
    <Flexbox className="channels" flexDirection="column">
      <div className="channel-header">
        Channelheader
      </div>
      <ul className="channel-list">
        {listChannels}
      </ul>
    </Flexbox>
  )
}

const Channel = props => (
  <button onClick={props.toggleChannel(props.channel)} className="channel-button">
    <div>
      {props.channel.title}
    </div>
    <div>
      status: {props.channel.status}
    </div>
  </button>
)

export default Chatbot
